require './lib/message_slack'

### ENERBOT: recomienda (texto_relevante) --- Ivan quería esto.
### ENERBOT: dame amor || i love you --- Entregar amor es uno de los principales objetivos del área "Continuous Love Delivery (CLD)" de ENERGON.
module ResponseHi
  extend MessageSlack
  def self.exec(data)
    text = case data.text
           when /(holi|hola)/
             ['Holiwis', "Oh, Hi <@#{data.user}>!", 'Hi desu!'].sample
           when /(i love u|i love you|dame amor)/
             "I love you #{rand 10_000}"
           when /(recomienda)/
             question = data.text.match(/enerbot\srecomienda\s(.*)/)
             q = question.captures[0].gsub(' ', '+')
             "lmgtfy.com/?q=#{q}"
           else
             ':ocean:'
           end

    send_message(text, data)
  end
end

module ResponseAgent
  extend MessageSlack
  def self.exec(data)
    send_message(['La Karen sabe responder a eso', 'Arresteme pronto', 'https://youtu.be/ibsJaAUUtPA'].sample, data)
  end
end

